const CHARGER_EFFICIENCY_FACTOR = flow.get('charger_efficiency_factor') || 0.90;
const INVERTER_EFFICIENCY_FACTOR = flow.get('inverter_efficiency_factor') || 0.90;
const STORAGE_COSTS = flow.get('storage_costs') || 0.00;

let price_max_allowed = 0.20;
const hours = msg.payload.viewer.homes[0].currentSubscription.priceInfo.today;
const price_current = msg.payload.viewer.homes[0].currentSubscription.priceInfo.current.total;
flow.set('price_current', price_current);

if (hours.length >= 24)
{
    let price_low = hours[0].total;
    let price_high = hours[0].total;

    for (let i = 0; i < 24; i++) 
    {
        if (hours[i].total < price_low)
        {
            price_low = hours[i].total;
        }

        if (hours[i].total > price_high)
        {
            price_high = hours[i].total;
        }
    }

    price_max_allowed = price_high * CHARGER_EFFICIENCY_FACTOR * INVERTER_EFFICIENCY_FACTOR - STORAGE_COSTS;

    flow.set('price_low', price_low);
    flow.set('price_high', price_high);
    flow.set('price_max_allowed', price_max_allowed);
}

msg.payload = Number(price_max_allowed);
node.status({text:"" + msg.payload});

return msg;

const SOC_MIN = 20; // %
const SOC_MAX = 100; // %

const ESS_STATE_KEEP_CHARGED = 9;
const ESS_STATE_OPTIMIZED = 10;

const BAT_CAPACITY = flow.get('bat_capacity'); // kWh

const CHARGER_EFFICIENCY_FACTOR = flow.get('charger_efficiency_factor') || 0.90;
const INVERTER_EFFICIENCY_FACTOR = flow.get('inverter_efficiency_factor') || 0.92;
const STORAGE_COSTS = flow.get('storage_costs') || 0.00;

const BAT_SOC = msg.payload.soc; // %
const PV_FORECAST = msg.payload.forecast; // kWh
const PV_FORECAST_PERC = Math.round((PV_FORECAST / BAT_CAPACITY) * 100); // %

// prices
const price_current = flow.get('price_current') || 0; // Eur
const price_low = flow.get('price_low') || 0; // Eur
const average_price = msg.payload.average_price; // Eur

const price_discharge_allowed = average_price / (CHARGER_EFFICIENCY_FACTOR * INVERTER_EFFICIENCY_FACTOR) + STORAGE_COSTS;
flow.set('price_discharge_allowed', price_discharge_allowed);

const remaining_energy = Math.round((BAT_CAPACITY * BAT_SOC / 100) * 100) / 100; // kWh
flow.set('remaining_energy', remaining_energy);

const missing_energy = Math.round((BAT_CAPACITY - remaining_energy) * 100) / 100; // kWh
flow.set('missing_energy', missing_energy);

// default values
let adaptive_soc = SOC_MIN; // %
let decision = 'Discharge';

if (price_current < 0)
{
    adaptive_soc = SOC_MAX;
    decision = 'Charge Full';
}
else
{
    const SOC_TARGET = SOC_MAX - PV_FORECAST_PERC;

    if (msg.payload.active && (BAT_SOC < SOC_TARGET))
    {
        // Calculate max. SoC for charging
        adaptive_soc = SOC_TARGET;
        decision = 'Charge';
    }
    else
    {
        if (price_current > price_discharge_allowed)
        {
            adaptive_soc = SOC_MIN;
            decision = 'Discharge';
        }
        else if (msg.payload.solarcharger_limited == true)
        {
            adaptive_soc = BAT_SOC - 10;
            decision = 'Surplus';
        }
        else 
        {
            adaptive_soc = BAT_SOC;
            decision = 'Hold';
        }
    }
}

// ensure we never get out of bounds
if (adaptive_soc < SOC_MIN) { adaptive_soc = SOC_MIN; }
if (adaptive_soc > SOC_MAX) { adaptive_soc = SOC_MAX; }

// set ess state based on desired soc
let ess_state = ESS_STATE_OPTIMIZED;
if (adaptive_soc >= SOC_MAX)
{
    ess_state = ESS_STATE_KEEP_CHARGED;
}

node.status({text:decision + " | " + BAT_SOC + "% -> " + adaptive_soc + "% | Cur: " + Math.round(price_current * 100) / 100 + "€ | Avg: " + Math.round(average_price * 100) / 100  + "€ | Dis: " + Math.round(price_discharge_allowed * 100) / 100 + "€"});

return [
    {'payload': adaptive_soc},
    {'payload': {
            'adaptive_soc': adaptive_soc,
            'decision': decision
        }
    },
    {'payload': ess_state}
];

flow.set('victron_soc', msg.payload);
